import base64
import random
key=random.randint(1,100)
print('key:',key)

info="base64加解密器只使用了简单的base64编码，加上随机的位移反转，在免杀效果上越是趋向应用的简单方式反而越有效，选择加解密器后shellcode和shellcode加载器都会采用加解密器的函数进行加密，如果不启用，那么默认也会将所有代码简单base64，运行时解码后通过eval来将字符串当作代码执行。"

def Encode(data):
    data=base64.b64encode(data)
    data=data[key:]+data[:key]
    return data


def Decode(data,cmd=False,key=0):
    data=data[len(data)-key:]+data[:len(data)-key]
    if cmd:
        data=base64.b64decode(data,validate=True).decode()
    else:
        data=base64.b64decode(data)

    return data




